// reducer takes two arguments, state and action
// inside the function is a switch case. default returns state. 
// return value of the reducer beccomes the new Redux state

const initialState = {
  user: {},
  transactions: [],
  portfolio: {}
}

export default (state = initialState, { type, payload }) => {
  switch (type) {
    case 'SET_USER':
      return {...state, user: payload};
    case 'SET_TRANSACTIONS':
      return {...state, transactions: payload};
    case 'SET_PORTFOLIO':
      return {...state, portfolio: payload};
    case 'CLEAR_USER':
      return {...state, user: {}};
    case 'UPDATE_USER_BALANCE':
      return {...state, user: {...state.user, balance: payload}};
    case 'ADD_TRANSACTION':
      return {...state, transactions: [...state.transactions, {...payload}]};
    case 'ADD_SHARE_TO_PORTFOLIO':
      if (state.portfolio[payload]) {
        console.log('yes', state.portfolio[payload].quantity)
        const total = state.portfolio[payload].quantity
        return {...state,
                portfolio: {
                  ...state.portfolio,
                  payload: {quantity: total++}
                }
        }
      } else {
        console.log('no', state.portfolio, payload)
      }
      return {...state};
    default:
      return state;
  }
};
