import React from 'react';
import { useSelector } from 'react-redux';

const Home = () => {
  const name = useSelector(state => state.user.name);
  const balance = useSelector(state => parseFloat(state.user.balance).toFixed(2));
  const portfolio = useSelector(state => state.user.portfolio);

  const leftColumnText = portfolio ? (
    <>
      <h1>Portfolio</h1>
      <br></br>
      <h6>Stocks</h6>
    </>
  ) : (
    null
  );
  const rightColumnText = name ? (
      <h3>Cash: ${balance}</h3>
    ) : (
      <h1>Please Signup or Login.</h1>
    );

  return <div className="row">
          <div className="column">{ leftColumnText }</div>
          <div className="column">{ rightColumnText }</div>
        </div>
};

export default Home;
