// reducer takes two arguments, state and action
// inside the function is a switch case. default returns state. 

export default (state = {}, { type, payload }) => {
  switch (type) {
    case 'SET_USER':
      return payload;
    case 'CLEAR_USER':
      return {};
    default:
      return state;
  }
};
