import React from 'react';
// import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';
import PurchaseForm from '../Components/PurchaseForm'
import Stock from '../Components/Stock'

const Home = () => {
  const user = useSelector(state => state.user);
  const balance = useSelector(state => parseFloat(state.user.balance).toFixed(2));
  const portfolio = useSelector(state => Object.entries(state.portfolio));
  const portfolioValue = useSelector(state => parseFloat(state.portfolioValue).toFixed(2));
  // let portfolioValue = useSelector(state => Object.keys(state.portfolio));

  // console.log(Object.keys(portfolio))
  console.log(portfolio[0] ? portfolio[0][1]['latestPrice'] : 'nope')

  console.log(portfolio.first ? portfolio.map( shareObj => {
    // console.log(shareObj)
    return shareObj[1]['latestPrice']
  }) : 'reduce failed')

  // var result = data.reduce(function(sum, item){
  //   return sum = sum+item.points;
  // },0);

  const leftColumn = user.name ? (
    <div className="column">
      <h1>Portfolio (${portfolioValue})</h1>
      {portfolio.map((stock, i) =>
          <Stock 
            key={i} 
            ticker={stock[0]}
            quantity={stock[1].quantity}
          />)}
    </div>
  ) : (
    null
  );

  const rightColumn = user.name ? (
      <div className="column">
        <h3>Cash: ${balance}</h3>
        <PurchaseForm />
      </div>
    ) : (
      null
    );

  const noUser = user.name ? (
      null
    ) : (
      <div className="auth-box">
        <h1>Please Signup or Login.</h1>
      </div>
    );

  return <div className="row">
          { leftColumn }
          { rightColumn }
          { noUser }
        </div>
};

export default Home;
