  user: {},
  transactions: [],
  portfolio: {}
}

export default (state = initialState, { type, payload }) => {
  switch (type) {
    case 'SET_USER':
      return {...state, user: payload};
    case 'SET_TRANSACTIONS':
      return {...state, transactions: payload};
