import React from 'react';
import { useSelector } from 'react-redux';
import Transaction from './Components/Transaction.js'

const pluralize = (qty) => {
  return qty === 1 ? "Stock" : "Stocks"
}

const Transactions = () => {
  // const name = useSelector(state => state.user.name);
  // const balance = useSelector(state => state.user.balance);
  const portfolio = useSelector(state => state.transactions);
  console.log(portfolio);



  const leftColumn = portfolio ? (
    <>
      <h2>Transactions</h2>
        {portfolio.map((transaction, i) =>
          <Transaction key={i} ticker={transaction.ticker} qty={transaction.qty} price={transaction.price * transaction.qty} pluralize={pluralize}/>)}
    </>
  ) : (
    <>
      <h1>Transactions</h1>
      <h3>Stocks</h3>
    </>
  );

  const rightColumn = null;

  return <div className="row">
          <div className="column">{ leftColumn }</div>
          <div className="column">{ rightColumn }</div>
        </div>
};

export default Transactions;
